import { useState } from "react";

export default function App() {
  const [items, setItems] = useState([]);

  function handleAddItems(item) {
    setItems((items) => [...items, item])
  }

  function handleDeleteItem(id) {
    setItems(items=>items.filter(item=>item.id !== id))
  }

  function handleToggleItem(id) {
    setItems(items => items.map(item => item.id === id ?
      {...item, packed: !item.packed} :
      item))
  }

  return (
    <div className="app">
      <Logo />
      <Form onAddItems={handleAddItems}/>
      <PackingList items={items} onDeleteItem={handleDeleteItem} onToggleItem={handleToggleItem}/>
      <Stats />
    </div>
  )
}

function Logo() {
  return (
    <h1>üå¥ Far Away</h1>
  )
}
function Form({ onAddItems }) {
  const [description, setDescription] = useState("");
  const [quantity, setQuantity] = useState(1);

  function handleSubmit(event) {
    event.preventDefault();

    if (!description) return;

    const newItem = {description, quantity, packed: false, id: Date.now() };
    console.log(newItem);

    onAddItems(newItem);

    setDescription("");
    setQuantity(1);
  }

  return (
    <form className="add-form" onSubmit={handleSubmit}>
      <h3>What do you need for your ü•∞ trip?</h3>
      <select
        value={quantity}
        onChange={(e) => setQuantity(e.target.
        value)}
      >
        {Array.from({length: 20}, (_, i) => i + 1).map((num) => {
          return (<option value={num} key={num}>{num}</option>)
        })}
      </select>
      <input type="text" placeholder="Item..." value={description} onChange={(e)=>setDescription(e.target.value)}/>
      <button>Add</button>
    </form>
  )
}

function PackingList({ items, onDeleteItem, onToggleItem }) {
  return (
    <div className="list">
      <ul>
        {items.map((item) => <Item item={item} onToggleItem={onToggleItem} onDeleteItem={onDeleteItem} key={item.id}/>)}
      </ul>
    </div>
  )
}

function Item({ item, onDeleteItem, onToggleItem }) {
  return (
    <li>
      <input type="checkbox" value={item.packed} onChange={() => onToggleItem(item.id)}/>
      <span style={item.packed ? {textDecoration: "line-through"} : {}}>
        {item.quantity} {item.description}
      </span>
      <button onClick={()=>onDeleteItem(item.id)}>‚ùå</button>
    </li>
  )
}

function Stats() {
  return (
    <footer className="stats">
      <em>
        üôà You have X items on your list, and you already packed X (X%)
      </em>
    </footer>
  )
}
